-- \"Galactic Colossus\" Experimental Assault Bot
Experimental_Choir_Aeon_01250 = {text = '<LOC Experimental_Aeon_010_010>[{i Choir}]: The Galactic Colossus uses an energy generator located in its \"eye\" to deliver its deadly payload. When the Colossus is destroyed, its chest opens and an unstable ball of energy is released. It will annihilate anything it touches.', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01250'}
Experimental_Choir_Aeon_01251 = {text = '<LOC Experimental_Aeon_010_020>[{i Choir}]: May the Galactic Colossus eliminate your enemies.', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01251'}
-- \"Tempest\" Submersible Battleship
Experimental_Choir_Aeon_01252 = {text = '<LOC Experimental_Aeon_020_010>[{i Choir}]: A massive battleship, the Tempest is armed with an Oblivion Cannon that hits targets from a great distance. It is supported by an internal Naval Factory, so the Tempest can create naval units as needed.', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01252'}
Experimental_Choir_Aeon_01253 = {text = '<LOC Experimental_Aeon_020_020>[{i Choir}]: The Tempest is now in your hands.', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01253'}
-- \"CZAR\" Flying Fortress
Experimental_Choir_Aeon_01254 = {text = '<LOC Experimental_Aeon_030_010>[{i Choir}]: A massive flying saucer, CZAR unleashes a blistering energy beam that obliterates anything it touches. The CZAR is also able to store air units.', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01254'}
Experimental_Choir_Aeon_01255 = {text = '<LOC Experimental_Aeon_030_020>[{i Choir}]: The CZAR is ready for launch. For the glory of the Aeon!', bank = 'Experimental_VO', cue = 'Experimental_Choir_Aeon_01255'}


