-- \"Monkeylord\" Spiderbot
Experimental_QAI_Cybran_01233 = {text = '<LOC Experimental_Cybran_010_010>[{i QAI}]: With its heavy Microwave Laser Generator, the \'Monkeylord\' Spiderbot can destroy countless units within seconds. Because of its size, it also crushes any units caught underfoot.', bank = 'Experimental_VO', cue = 'Experimental_QAI_Cybran_01233'}
Experimental_Ops_Cybran_01234 = {text = '<LOC Experimental_Cybran_010_020>[{i Ops}]: Monkeylord ready for battle.', bank = 'Experimental_VO', cue = 'Experimental_Ops_Cybran_01234'}
Experimental_Ops_Cybran_01235 = {text = '<LOC Experimental_Cybran_010_030>[{i Ops}]: Spiderbot is ready to attack.', bank = 'Experimental_VO', cue = 'Experimental_Ops_Cybran_01235'}
-- \"Reaver\" Gunship
Experimental_QAI_Cybran_01236 = {text = '<LOC Experimental_Cybran_020_010>[{i QAI}]: The Reaver is a flying heavy weapons platform. Armed with a Heavy Iridium Rocket Racks and Heavy Electron Bolters, it attacks ground units with crippling force.', bank = 'Experimental_VO', cue = 'Experimental_QAI_Cybran_01236'}
Experimental_Ops_Cybran_01237 = {text = '<LOC Experimental_Cybran_020_020>[{i Ops}]: Your Reaver is ready for field testing, Commander.', bank = 'Experimental_VO', cue = 'Experimental_Ops_Cybran_01237'}
-- \"Scathis\" Mobile Rapid Fire Artillery
Experimental_QAI_Cybran_01240 = {text = '<LOC Experimental_Cybran_030_010>[{i QAI}]: Because of its mobility, the Scathis can unleash a barrage of artillery against nearly any target. Its range is excellent, and its firing speed has been greatly enhanced.', bank = 'Experimental_VO', cue = 'Experimental_QAI_Cybran_01240'}
Experimental_Ops_Cybran_01242 = {text = '<LOC Experimental_Cybran_030_020>[{i Ops}]: Scathis now online.', bank = 'Experimental_VO', cue = 'Experimental_Ops_Cybran_01242'}


