Experimental_EarthCom_UEF_01219 = {text = '<LOC Experimental_UEF_010_010>[{i EarthCom}]: The Fatboy is a land-based battleship. Tri-barreled cannons positioned along its perimeter can unleash a devastating artillery barrage, and the rear construction bay offers excellent reinforcement capabilities.', bank = 'Experimental_VO', cue = 'Experimental_EarthCom_UEF_01219'}
