Experimental_EarthCom_UEF_01223 = {text = '<LOC Experimental_UEF_020_010>[{i EarthCom}]: The Atlantis Aircraft Carrier is the premier air projection component in the EarthCom navy. It stores air units and comes standard with light anti-air support and torpedo tubes.', bank = 'Experimental_VO', cue = 'Experimental_EarthCom_UEF_01223'}
Experimental_EarthCom_UEF_01224 = {text = '<LOC Experimental_UEF_020_020>[{i EarthCom}]: The Atlantis is ready for deployment.', bank = 'Experimental_VO', cue = 'Experimental_EarthCom_UEF_01224'}
-- \"Mavor\" Long Range Strategic Artillery
Experimental_EarthCom_UEF_01228 = {text = '<LOC Experimental_UEF_030_010>[{i EarthCom}]: The Mavor\'s firing chamber has been modified to fire tactical nuclear shells extremely long distances. Its range is unsurpassed by any known weapon.', bank = 'Experimental_VO', cue = 'Experimental_EarthCom_UEF_01228'}
